css:
.admin-menu {
    color: #206E9F;
}

.admin-menu-section {
    color: inherit;
    text-align: center;
    background-color: inherit; /*#fff;*/
    margin: 1.25em 0;
}

.admin-menu-section-main {
    border: 1px solid #026BC8 ;
}

.admin-menu-section-main a {
 color: #026BC8;
   background-color: inherit;
   display: block;
    min-width: 18em; /*21em;*/
    min-height: 6.2em;
    padding-top: 3.4em;
    font-family: "OpenSans-semibold", sans-serif;
    text-decoration: none;
}

@media (max-width: 991px) {
  .admin-menu-section-sub>div:first-of-type {
    border-bottom: 1px solid #026BC8;
  }
  div#pbid-GotoCSSExport-container {
      border-bottom: none;
  }
}

.admin-menu-section-sub {
    display: block;
    min-width: 1em;
    min-height: 5.2em;
    border-bottom: 1px solid #026BC8;
    border-right: 1px solid #026BC8;
    border-left: 1px solid #026BC8;
}

.admin-menu-section-sub a {
        color: #026BC8;
    background-color: inherit;
  display: block;
    min-width: 1em;
    min-height: 5.2em;
    padding-top: 2.4em;
    text-decoration: none;
}

/* The import link */
#pbid-adminMainBlock{
  height: 11.5em;
}
#pbid-GotoImport{
  padding-top:6em;
  padding-bottom:4em;
}

@media (min-width: 992px) {
  .admin-menu-section-sub>div:first-of-type {
      border-$end: 1px solid #026BC8;
  }
}

div#pbid-GotoCSSExport-container {
    border-$end: none;
}

a#pbid-GotoVDC {
    background: url(plugins/banner-sspb-0.1/images/virtual-domain-composer.svg) center 12px no-repeat;
}

a#pbid-GotoVDCExport {
    background: url(plugins/banner-sspb-0.1/images/export-domains.svg) center 12px no-repeat;
}

a#pbid-GotoVDCRoles {
    background: url(plugins/banner-sspb-0.1/images/virtual-domain-roles.svg) center 12px no-repeat;
}

a#pbid-GotoVPC {
    background: url(plugins/banner-sspb-0.1/images/visual-page-composer.svg) center 12px no-repeat;
}

a#pbid-GotoVPCExport {
    background: url(plugins/banner-sspb-0.1/images/export-pages.svg) center 12px no-repeat;
}

a#pbid-GotoVPCRoles {
    background: url(plugins/banner-sspb-0.1/images/page-roles.svg) center 12px no-repeat;
}

a#pbid-GotoCSSManager {
    background: url(plugins/banner-sspb-0.1/images/css-manager.svg) center 12px no-repeat;
}

a#pbid-GotoCSSExport {
    background: url(plugins/banner-sspb-0.1/images/export-css-stylesheets.svg) center 12px no-repeat;
}
a#pbid-GotoImport {
    background: url(plugins/banner-sspb-0.1/images/import-pb-artifacts.svg) center 3.5em no-repeat;
}

/* Start page application list Style */
.app-list-container {
    border: 1px solid #026BC8;
}

.app-list-container>label {
    padding: 1.20em 1.20em 0.8em 1.20em;
    font-family: "OpenSans-semibold", sans-serif;
}

.app-list-container>div {
    border-top: 1px solid #026BC8;
    padding: 1.25em 1.25em 1em;
}

.page-id {
    font-family: "OpenSans-semibold", sans-serif;
}

.run-page a {
    color: #026BC8;
    float: $end;
    background: url(plugins/banner-sspb-0.1/images/view-page.svg) center 14px no-repeat;
    height: 35px;
    padding-$start: 2.5em;
}

.edit-page a {
    color: #026BC8;
    float: $end;
    background: url(plugins/banner-sspb-0.1/images/edit-page.svg) center 14px no-repeat;
    height: 35px;
    padding-$start: 2.5em;
}

.more-option a {
    float: $end;
    background: url(plugins/banner-sspb-0.1/images/more-info.svg) center 14px no-repeat;
    height: 35px;
    padding-$start: 2.5em;
}

.pb-label {
    font-weight: normal;
}

#pbid-pageFilter-container {
    position: relative;
    top: 16px;
    $start: -15%; 
    margin: auto;
    width: 0; 
    height: 0; 
}

#pbid-pageFilter {
  width: 18em;
}

 .pb-record-control{
   padding: 0;
}

.pb-datetime {
    font-size: 12px;
}

 .pbid-PageListBlock {
    color: #4d4640;
}
