components.onUpdate:
if($ownerPagesBlock_genericData){
$securityBlock.$visible = true;
$developerPrivilegesBlock.$visible = true;
if(this.currentRecord){
$ownerPagesBlock_owner=this.currentRecord.TYPE_OWNER
$assigningPrivileges=this.currentRecord.ALLOW_MODIFY
$selectedPage = this.currentRecord.NAME
}
$userRoleGrid.$load()
$profileGrid.$load()
}else{
$ownerPagesBlock_owner='';
$assigningPrivileges='N';
$securityBlock.$visible = false;
$developerPrivilegesBlock.$visible = false;
}

if($ownerPagesBlock_genericData){
var genericDataDetails =  $ownerPagesBlock_genericDataDS.data.filter(function(x){ return x.SOURCE_TYPE_ID==$ownerPagesBlock_genericData})[0];

genericDataDetails.ALLOW_MODIFY=$assigningPrivileges 

$genericData.$put({'id':genericDataDetails.id , 'SOURCE_TYPE_ID':$ownerPagesBlock_genericData , 'SOURCE_TYPE':$ownerPagesBlock_options, 'ALLOW_ALL':$assigningPrivileges }, null, function(){

if($assigningPrivileges=='Y'){
alert("${message(code:'pbadmDeveloperPageSecurity.securityform.enableall')}", {type:"success", flash:true}); 
}else{
	alert("${message(code:'pbadmDeveloperPageSecurity.securityform.disableall')}", {type:"success", flash:true}); 
}
});
}

components.onError:
var errorMessage =response.data.errors? response.data.errors.errorMessage: null;
if(errorMessage&&errorMessage.indexOf("ORA-20000") > -2){
 alert(response.data.errors.errorMessage, {type: "error"});
}else 
if(errorMessage&&errorMessage){
    alert(response.data.errors.errorMessage, {type: "error"});
}else {
    alert(errorMessage?errorMessage:response.data, {type:"error"});
  }

components.onSaveSuccess:
