onLoad.ext:
function download(p){
    if(!p) return;
    var theAnchor = $('<a />')
        .attr('href', p.header+p.data)
        .attr('download', p.fileName)
        // Firefox does not fires click if the link is outside
        // the DOM
        .appendTo('body');

    theAnchor[0].click(); 
    theAnchor.remove();
}

//Define keys to remove using + to avoid replacement with PB variables
var keys={};
keys[ "$"+"resolved"]= true;
keys["$"+"$"+"hashKey"]= true;
keys["$"+"promise"]=true;

// remove additional properties added by Angular resource when pretty print page source
function jsonFilter(key, value) {
    return keys[key]?undefined:value;
}


if (data && data.constantName){
    var params = {
          fileName: 'pages.'+data.constantName+'.json',
          header: 'data:text/csv;charset=utf8,',
          data: encodeURIComponent(JSON.stringify(data,jsonFilter,3))
    };
    download(params);
} else {
    alert('Unable to save data - unexpected format.');
}

