DECLARE
v_count NUMBER(10) := 0;
enableDeveloperSecurity_lv varchar2(50) := 'pagebuilder.security.enableDeveloperSecurity';
preventImportByDeveloper_lv varchar2(50) := 'pagebuilder.security.preventImportByDeveloper';
developerReadOnly_lv varchar2(50) := 'pagebuilder.security.developerReadOnly';
GUROCFG_GUBAPPL_APP_ID_lv varchar2(5) := 'EXTZ';
GUROCFG_TYPE_lv varchar2(10) := 'boolean';
GUROCFG_USER_ID_lv varchar2(10) := 'BANNER';
GUROCFG_DATA_ORIGIN_lv varchar2(10) := 'Banner';

BEGIN

SELECT COUNT(*) INTO  v_count FROM GUROCFG where  GUROCFG_TYPE = GUROCFG_TYPE_lv and GUROCFG_GUBAPPL_APP_ID = GUROCFG_GUBAPPL_APP_ID_lv and GUROCFG_NAME = enableDeveloperSecurity_lv;
if v_count = 0 then
insert into GUROCFG (GUROCFG_NAME,GUROCFG_VALUE,GUROCFG_TYPE,GUROCFG_ACTIVITY_DATE,GUROCFG_GUBAPPL_APP_ID,GUROCFG_USERPREF_IND,GUROCFG_USER_ID,GUROCFG_DATA_ORIGIN) 
values (enableDeveloperSecurity_lv, (select decode(:enableDeveloperSecurity,'', 'false','Y','true','false') from dual) ,GUROCFG_TYPE_lv,sysdate, GUROCFG_GUBAPPL_APP_ID_lv,'N',GUROCFG_USER_ID_lv,GUROCFG_DATA_ORIGIN_lv);
else 
update GUROCFG set GUROCFG_VALUE = (select decode(:enableDeveloperSecurity,'', 'false','Y','true','false') from dual) where GUROCFG_TYPE = GUROCFG_TYPE_lv and GUROCFG_GUBAPPL_APP_ID = GUROCFG_GUBAPPL_APP_ID_lv and GUROCFG_NAME = enableDeveloperSecurity_lv;
end if;

SELECT COUNT(*) INTO  v_count FROM GUROCFG where  GUROCFG_TYPE = GUROCFG_TYPE_lv and GUROCFG_GUBAPPL_APP_ID = GUROCFG_GUBAPPL_APP_ID_lv and GUROCFG_NAME = preventImportByDeveloper_lv;
