values (preventImportByDeveloper_lv, (select decode(:preventImportByDeveloper,'', 'false','Y','true','false') from dual) ,GUROCFG_TYPE_lv,sysdate, GUROCFG_GUBAPPL_APP_ID_lv,'N',GUROCFG_USER_ID_lv,GUROCFG_DATA_ORIGIN_lv);
else 
update GUROCFG set GUROCFG_VALUE = (select decode(:preventImportByDeveloper,'', 'false','Y','true','false') from dual) where GUROCFG_TYPE = GUROCFG_TYPE_lv and GUROCFG_GUBAPPL_APP_ID = GUROCFG_GUBAPPL_APP_ID_lv and GUROCFG_NAME = preventImportByDeveloper_lv;
end if;

SELECT COUNT(*) INTO  v_count FROM GUROCFG where  GUROCFG_TYPE = GUROCFG_TYPE_lv and GUROCFG_GUBAPPL_APP_ID = GUROCFG_GUBAPPL_APP_ID_lv and GUROCFG_NAME = developerReadOnly_lv;
