else 
update GUROCFG set GUROCFG_VALUE = (select decode(:developerReadOnly,'', 'false','Y','true','false') from dual) where GUROCFG_TYPE = GUROCFG_TYPE_lv and GUROCFG_GUBAPPL_APP_ID = GUROCFG_GUBAPPL_APP_ID_lv and GUROCFG_NAME = developerReadOnly_lv;
end if;

END;
