css:
body, html {
  font-size: 100%;
  font-family: OpenSans-regular;
}

#content {
  font-size: 100%;
  font-family: OpenSans-regular;
}

.pb-form-item-container {
  padding-top: 1em;
}

input.pb-item.pb-text,
input.pb-item.pb-number,
input.pb-item.pb-datetime,
input.pb-item.pb-email,
input.pb-item.pb-tel
{
  width: 18.75em;
  border-radius: 4px;
  border: 1px solid #869FB1;
  box-shadow: inset 4px 4px 4px #E2EAEF;
  padding: 1em 0.75em;
  box-sizing: border-box;
  webkit-box-sizing: border-box;
  background-color: #FFFFFF;
  font-size: 0.875em;
 }
 input.pb-item:focus {
        border: 1px solid #206E9F;
        box-shadow: inset 0px 2px 2px #CBE2E8;
        color: #4F585F;
 }
input.pb-item.hasDatepicker{
  height:inherit;
}

label.pb-label{
  size: 1em;
  color: #4F585F;
  font-weight: 400; 
  display:block;
}
label.pb-label.pb-boolean{
  display:inline-block;
}

select.pb-item.pb-select {
    color:#4F585F;
    border: 2px solid #869FB1;
    box-shadow: inset 0px -4px #DEE5E7;
    width: 18.75em;   
   padding: 1em 0.75em;
   font-size: 0.875em;
    -moz-border-radius       : 4px;
    -webkit-border-radius    : 4px;
    border-radius:4px;
}

select.pb-select:focus {
  background-color: #FFFFFF;
  border: 2px solid #206E9F; }

textarea.pb-textArea {
    font-size: 0.875em;
    width:18.75em;
    height: 8.125em;
    border-radius: 0.25em;
    border: 2px solid #869FB1;
    box-shadow: inset 4px 4px 4px #E2EAEF;
    padding: 0.75em;
    background-color: #FFFFFF;
    resize: none;
    line-height: 172%;
}
textarea:focus {
    border: 2px solid #206E9F;
    box-shadow: inset 0px 2px 2px #CBE2E8;
    color: #4F585F;
}

/* Styling of radio button like xe but working with PB */

input[type='radio'] {
    -webkit-appearance: none;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    outline: none;
    border: 2px solid gray;
	cursor: pointer;
	margin-right: 10px;
    margin-left: 20px;
    font-weight: normal;
}
input[type='radio']:disabled {
	border-color:#B2B3B7;
	pointer-events : none;
}

input[type='radio']:before {
	content: '';
	display: block;
	width: 60%;
	height: 60%;
	margin: 20% auto;
	border-radius: 50%;
}

input[type='radio']:focus {
	box-shadow: 0 0 0 2px #51ABFF;
}

input[type="radio"]:checked:before {	
	background: #026BC8;
}

input[type="radio"]:checked {
	-webkit-appearance: none;
	width: 20px;
	height: 20px;
	border-radius: 50%;
	outline: none;
	border: 2px solid gray;
	cursor: pointer;
	margin-right: 10px;
	margin-left: 20px;
	font-weight: normal;
	border-color:#026BC8;
}

input[type="radio"]:checked:disabled:before {
	pointer-events : none;
	background: #D9D9D9;
}

input[type="radio"]:checked:disabled {
	pointer-events : none;
	border-color:#D9D9D9;
}

/* Form Submit touches item - fix this*/
.pb-form-submit {
  padding-top: 1em;

}
.pb-form-submit-button{ /*primary button*/
    font-size: 0.875em;
    background-color: #206E9F;
    border: 1px solid #206E9F;
    box-shadow:inset -0px -4px 0px #0C5482;
    color: #FFFFFF;
    border-radius: 4px;
    padding: 0.875em 5.75em;
}
