css:
body, html {
  font-size: 100%;
  font-family: OpenSans-regular;
}

#content {
  font-size: 100%;
  font-family: OpenSans-regular;
}

.pb-form-item-container {
  padding-top: 1em;
}

input.pb-item.pb-text,
input.pb-item.pb-number,
input.pb-item.pb-datetime,
input.pb-item.pb-email,
input.pb-item.pb-tel
{
  width: 18.75em;
  border-radius: 4px;
  border: 1px solid #869FB1;
  box-shadow: inset 4px 4px 4px #E2EAEF;
  padding: 1em 0.75em;
  box-sizing: border-box;
  webkit-box-sizing: border-box;
  background-color: #FFFFFF;
  font-size: 0.875em;
 }
 input.pb-item:focus {
        border: 1px solid #206E9F;
        box-shadow: inset 0px 2px 2px #CBE2E8;
        color: #4F585F;
 }
input.pb-item.hasDatepicker{
  height:inherit;
}

label.pb-label{
  size: 1em;
  color: #4F585F;
  font-weight: 400; 
  display:block;
}
label.pb-label.pb-boolean{
  display:inline-block;
}

select.pb-item.pb-select {
    color:#4F585F;
    border: 2px solid #869FB1;
    box-shadow: inset 0px -4px #DEE5E7;
    width: 18.75em;   
   padding: 1em 0.75em;
   font-size: 0.875em;
    -moz-border-radius       : 4px;
    -webkit-border-radius    : 4px;
    border-radius:4px;
}

select.pb-select:focus {
  background-color: #FFFFFF;
  border: 2px solid #206E9F; }

textarea.pb-textArea {
    font-size: 0.875em;
    width:18.75em;
    height: 8.125em;
    border-radius: 0.25em;
    border: 2px solid #869FB1;
    box-shadow: inset 4px 4px 4px #E2EAEF;
    padding: 0.75em;
    background-color: #FFFFFF;
    resize: none;
    line-height: 172%;
}
textarea:focus {
    border: 2px solid #206E9F;
    box-shadow: inset 0px 2px 2px #CBE2E8;
    color: #4F585F;
}

/* Styling of radio button like xe but working with PB */

input[type=radio]:not(old){  
  margin  : 0;
  padding : 0;
  opacity : 0;
}

input[type="radio"] + span {
  background-repeat: no-repeat;
  background-position: $start center;
  background-size: 1.2em;
  line-height:172%;  
  cursor: pointer;
  padding-$start: 30px;
  background-image: url(plugins/banner-sspb-0.1/BannerXE/lib/xe-components/css/images/radiobutton/radio-button-normal.svg);
}

input[type="radio"]:checked + span {
  color: #1C8445;
  background-image: url(plugins/banner-sspb-0.1/BannerXE/lib/xe-components/css/images/radiobutton/radio-button-selected.svg)  
}

/* Styling of checkbox */
.pb-boolean input[type="checkbox"] {
    display:none;
}

.pb-boolean .pb-item.pb-boolean+label {
  background-repeat: no-repeat;
  background-position: $start center;
  background-size: 1em 1em;
  line-height: 172%;
  cursor: pointer;
  padding-$start: 30px;
  background-image: url(plugins/banner-sspb-0.1/BannerXE/lib/xe-components/css/images/checkbox/check-box-normal.svg);
}
.pb-boolean .pb-item.pb-boolean:checked+label {
  background-image: url(plugins/banner-sspb-0.1/BannerXE/lib/xe-components/css/images/checkbox/check-box-selected.svg);
}

/* Form Submit touches item - fix this*/
.pb-form-submit {
  padding-top: 1em;

}
.pb-form-submit-button{ /*primary button*/
    font-size: 0.875em;
    background-color: #206E9F;
    border: 1px solid #206E9F;
    box-shadow:inset -0px -4px 0px #0C5482;
    color: #FFFFFF;
    border-radius: 4px;
    padding: 0.875em 5.75em;
}
