DECLARE
  l_cnt    INTEGER;
  l_deptno INTEGER :=:DEPTNO;
  l_empno INTEGER :=:EMPNO;
BEGIN
  IF :DEPTNO IS NOT NULL AND :DNAME IS NOT NULL THEN 
    SELECT COUNT(*) INTO l_cnt FROM dept WHERE deptno=:DEPTNO;
    IF l_cnt=0 THEN
      INSERT INTO dept
        (deptno,dname,loc
        ) VALUES
        (:DEPTNO,:DNAME,:LOC
        );
    ELSE
      UPDATE dept SET dname=:DNAME, loc=:LOC WHERE deptno=:DEPTNO;
    END IF;
  END IF;
  SELECT COUNT(*) INTO l_cnt FROM emp WHERE empno=:EMPNO;
  IF l_cnt      =0 THEN
    IF :DEPTNO IS NULL THEN
      SELECT deptno INTO l_deptno FROM dept WHERE dname=:DNAME;
    END IF;
   IF :EMPNO IS NULL THEN
     SELECT MAX(empno)+1 INTO l_empno FROM emp;
   END IF;
    INSERT
    INTO emp
      (
        empno,
        ename,
        job,
        mgr,
        hiredate,
        sal,
        comm,
        deptno
      )
      VALUES
      (
        l_empno,
        :ENAME,
        :JOB,
        :MGR,
        :HIREDATE,
        :SAL,
        :COMM,
        l_deptno
      );
  ELSE
    UPDATE emp
    SET ename  =NVL(:ENAME,NULL),
      job      =NVL(:JOB,NULL),
      mgr      =NVL(:MGR,NULL),
      hiredate =:HIREDATE,
      sal      =NVL(:SAL,NULL),
      comm     =NVL(:COMM,0),
      deptno   =NVL(:DEPTNO,NULL)
    WHERE empno=:EMPNO;
  END IF;
  --Update this (colon)id on the client - if we created a new record it will not be set
  --(colon)id=(colon)EMPNO||','||(colon)DEPTNO;
END;

